SET NAMES UTF8;
DROP DATABASE IF EXISTS mfresh;
CREATE DATABASE mfresh CHARSET=UTF8;
USE mfresh;

/**1.用户表**/
CREATE TABLE mf_user(
    uid INT PRIMARY KEY AUTO_INCREMENT, /*用户编号*/
    uname VARCHAR(32),                  /*用户名*/
    upwd VARCHAR(32),                   /*密码*/
    phone VARCHAR(11)                   /*电话*/
);
