/**2.产品表**/
CREATE TABLE mf_product(
    pid INT PRIMARY KEY AUTO_INCREMENT,
    title1 VARCHAR(64),                 /*一级名称，主名称*/
    title2 VARCHAR(64),                 /*二级名称，广告语*/
    pic VARCHAR(64),
    type INT ,                          /*1-净化器 2-滤网*/
    model VARCHAR(64),                  /*型号*/
    func VARCHAR(64),                   /*功能*/
    noise VARCHAR(64),                  /*噪音水平*/
    wind VARCHAR(64),                   /*风量*/
    applyTo VARCHAR(64),                /*适用对象*/
    size VARCHAR(64),                   /*适用面积*/
    level VARCHAR(64),                  /*能效等级*/
    price FLOAT(10,2),                  /*价格*/
    detail VARCHAR(2048)
);
